void romFrecuencias::leerA(){

	sc_uint<9> addr;

	while(true){

		dirA->read( addr );
		datoA->write( (sc_uint<16>)Tablas[addr.to_int()] );

	};
}


void romFrecuencias::leerB(){

	sc_uint<9> addr;

	while(true){

		dirB->read( addr );
		datoB->write( (sc_uint<16>)Tablas[addr.to_int()] );

	};

}